import './sidebar.scss'
import {useState} from "react";
import bigLogo from '../../assets/img/onex-logo.svg'
import logo from '../../assets/img/logo.png'
const {Link} = require("react-router-dom");

function Sidebar() {
    const [isActive, setIsActive] = useState(false)
    const sidebarList = [
        {
            name: 'Partners',
            icon: 'sidenav-icon material-icons',
            iconName: 'business',
            routeName: 'companies',
            role: 1,
            isOpen: false,
            dropdownLink: [
                {
                    name: ' Partner',
                    route: 'companies',
                },
                {
                    name: 'Add New Partner',
                    route: 'companies/create'
                },
            ],
        },
        {
            name: 'Pickup Points',
            routeName: 'pickUpPoints',
            isOpen: false,
            icon: 'sidenav-icon material-icons',
            iconName: 'room',
            role: 1,
            roleElse: 2,
            dropdownLink: [
                {
                    name: ' Pickup Points',
                    route: 'pickUpPoints',
                },
                {
                    name: 'Add New Pickup Point',
                    route: 'pickUpPoints/create'
                },
            ],
        },
        {
            name: 'Questions',
            routeName: 'questions',
            isOpen: false,
            role: '',
            icon: 'sidenav-icon material-icons',
            iconName: 'help_outline',
            dropdownLink: [
                {
                    name: ' Questions',
                    route: 'questions',
                },
                {
                    name: 'Add New Question',
                    route: 'questions/create'
                },
            ],
        },
        {
            name: 'Voices',
            routeName: 'voices',
            image: '/images/voice.svg',
            isOpen: false,
            role: '',
            icon: 'sidenav-icon material-icons',
            iconName: 'mic',
            dropdownLink: [
                {
                    name: ' Voices',
                    route: 'voices',
                },
                {
                    name: 'Add New Voice',
                    route: 'voices/create'
                },
            ],
        },
        {
            name: 'Team',
            routeName: 'workers',
            isOpen: false,
            icon: 'sidenav-icon material-icons',
            iconName: 'engineering',
            role: 1,
            roleElse: 2,
            dropdownLink: [
                {
                    name: ' Team',
                    route: 'workers',
                },
                {
                    name: 'Add New Member',
                    route: 'workers/create'
                },
            ],
        },
        {
            name: 'Users',
            routeName: 'users',
            isOpen: false,
            icon: 'sidenav-icon material-icons',
            iconName: 'group',
            role: 1,
            roleElse: 2,
            dropdownLink: [
                {
                    name: ' Users',
                    route: 'users',
                },
                {
                    name: 'Add New User',
                    route: 'users/create'
                },
            ],
        },
        {
            name: 'History',
            routeName: 'histories',
            icon: 'sidenav-icon material-icons',
            iconName: 'history',
            isOpen: false,
            role: '',
        },
    ]

    function dropdown() {

    }

    function sideNavToggle() {
        console.log(isActive);
        setIsActive(!isActive)
    }

    function headerListItemIconWrapper() {

    }

    function logout() {

    }

    return (
        <div className={'sideBar-wrapper'}>
            <div className={'logo-wrapper'}>
                <Link to={{path: '/'}} className={`brand-logo`}>
                    <img src={isActive ? bigLogo : logo} alt="logo" className={`logo ${isActive ? 'active' : ''}`}/>
                </Link>
                <div className={`burger-wrapper ${isActive ? 'right' : ''}`}>
                    <div className="burger" onClick={(e) => sideNavToggle(e)}>
                        <span className={`burger-line ${isActive ? 'db' : ''}`}></span>
                        <span className={`burger-line2 ${isActive ? 'db' : ''}`}></span>
                        <span className={`burger-line ${isActive ? 'db' : ''}`}></span>
                        <span className={`material-icons burger-arrow-icon ${isActive ? 'dn' : ''}`}>east</span>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Sidebar
